/* ***** iteration: ***** */
/* New approach: */
#define map_int_char_p_forall_nondet(h,i,k,v) \
        for(map_int_char_p_init_iterator ((h),&i); \
            map_int_char_p_next_iteration ((h),&i,&k,&v);)
/* Operator for non-deterministic hash iteration.  This is non-deterministic even if
 * ERWIN_REQUIRE_DETERMINISM is #defined.
 *
 * This iterates over keys and values.  You need a map_iterator_t as a temporary for
 * using this.  See map_int_char_p_forall for details.
 */

#define map_int_char_p_forall_ptr_nondet(h,i,k,v) \
        for(map_int_char_p_init_iterator ((h),&i); \
            map_int_char_p_next_iteration_ptr ((h),&i,&k,&v);)
/* Operator for non-deterministic hash iteration.  This is non-deterministic even if
 * ERWIN_REQUIRE_DETERMINISM is #defined.
 *
 * This iterates over keys and pointers to values.  You need a map_iterator_t as a
 * temporary for using this.  See map_int_char_p_forall for details.  Iterating over pointers
 * allows you to change entries in the table on the fly.
 */

#define map_int_char_p_forall_values_nondet(h,i,v) \
        for(map_int_char_p_init_iterator ((h),&i); \
